package advent

fun main(args: Array<String>) {
    var floor = 0

    input.forEachIndexed { i, c->
        when(c) {
            '(' -> floor++
            ')' -> floor--
        }

        if (floor == -1) {
            println(i+1)
            return
        }
    }

    println(floor)

}

